                err.Reason = "Error: Virtual failure!!"
                //选择步骤：
                //但是如果我们想要在事务失败时将错误消息回发给客户端，这很有用
                //如果原因为空，那么交易成功完成，
                //否则我们回滚整个返回的响应体，
                //header和Cookie，状态代码以及此事务中的所有内容
                t.Complete(err)
            }
        })
        ctx.BeginTransaction(func(t *context.Transaction) {
            t.Context().HTML("This will sent at all cases because it lives on different transaction and it doesn't fails")
            // *如果我们没有任何'throw error'逻辑，则不需要scope.Complete（）
        })
        // OPTIONALLY，取决于用法：
        //无论如何，在上下文的事务中发生的情景都会发送给客户端
        ctx.HTML("Let's add a second html message to the response, " +
            "if the transaction was failed and it was request scoped then this message would " +
            "not been shown. But it has a transient scope(default) so, it is visible as expected!")
    })
    app.Run(iris.Addr(":8080"))
}

